page.vertex:
#define GLSLIFY 1
varying vec2 vUv;
uniform float time;
varying float vVor;
//	https://www.shadertoy.com/view/lsjGWD
//	by Pietro De Nicola
//
#define OCTAVES   		1		// 7
#define SWITCH_TIME 	60.0		// seconds

float t()               {return 0.5;}
float function()        {return mod(t(),4.0);}
bool  multiply_by_F1()  {return mod(t(),8.0)  >= 4.0;}
bool  inversef()         {return mod(t(),16.0) >= 8.0;}
float distance_type()	{return mod(t()/16.0,4.0);}

vec2 hash( vec2 p ){
	p = vec2( dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3)));
	return fract(sin(p)*43758.5453);
}

float voronoi( in vec2 x ){
	vec2 n = floor( x );
	vec2 f = fract( x );

	float F1 = 8.0;
	float F2 = 8.0;

	for( int j=-1; j<=1; j++ )
		for( int i=-1; i<=1; i++ ){
			vec2 g = vec2(i,j);
			vec2 o = hash( n + g );

			o = 0.5 + 0.41*sin( time + 6.2831*o );
			vec2 r = g - f + o;

		float d = 	distance_type() < 1.0 ? dot(r,r)  :				// euclidean^2
				  	distance_type() < 2.0 ? sqrt(dot(r,r)) :			// euclidean
					distance_type() < 3.0 ? abs(r.x) + abs(r.y) :		// manhattan
					distance_type() < 4.0 ? max(abs(r.x), abs(r.y)) :	// chebyshev
					0.0;

		if( d<F1 ) {
			F2 = F1;
			F1 = d;
		} else if( d<F2 ) {
			F2 = d;
		}
    }

	float c = function() < 1.0 ? F1 :
			  function() < 2.0 ? F2 :
			  function() < 3.0 ? F2-F1 :
			  function() < 4.0 ? (F1+F2)/2.0 :
			  0.0;

	if( multiply_by_F1() )	c *= F1;
	if( inversef() )			c = 1.0 - c;

    return c;
}

float fbm( in vec2 p ){
	float s = 0.0;
	float m = 0.0;
	float a = 0.5;

	for( int i=0; i<OCTAVES; i++ ){
		s += a * voronoi(p);
		m += a;
		a *= 0.5;
		p *= 2.0;
	}
	return s/m;
}

void main() {
    vUv = uv;

    vec3 newPos = position;
    float vor = voronoi(newPos.xz*(0.01 + (sin(time*2.5)+1.0)*0.5*0.005) + time);
    vor = pow(1.-vor,2.0);
    vVor = vor;
    newPos.y += vor*15.0;
    gl_Position = projectionMatrix * modelViewMatrix * vec4( newPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
varying float vVor;

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void main() {
    vec2 newUv = vUv;
    vec3 colorA = vec3(newUv.x,0.25,newUv.y);
    vec3 colorAHSB = rgb2hsb(colorA);
    colorAHSB.r += 0.3;
    vec3 colorB = hsb2rgb(colorAHSB);
    gl_FragColor = vec4(mix(colorA,colorB,vVor),1.0);
}
