page.vertex:
#define GLSLIFY 1
varying vec2 vUv;
varying vec2 vDiff;
uniform float time;
void main() {
    vUv = uv;
    vec3 newPos = position;
    newPos -= vec3(50.,0.0,-50.);
    newPos*= 1.5;

    vec2 diff = newPos.xz - vec2(75.,-35.);
    vDiff = diff;
    vec3 finalPos = newPos;
    float smoothl = 15.;
    float value = smoothstep(length(diff)-smoothl,length(diff)+smoothl,90. + sin(time)*45.);
    finalPos.y += sin(value*15.)*15.;

    gl_Position = projectionMatrix * modelViewMatrix * vec4(finalPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
varying vec2 vDiff;
void main() {
    vec2 newUv = vUv;
    newUv -= vec2(0.5,0.5);

    float stValue = (sin(time)+1.0)*0.5;
    float v1 = smoothstep(stValue-0.15,stValue+0.15,length(newUv));
    float v2 = (sin(length(newUv)*10.*cos(time*1.5)+time)+1.0)*0.5;
    gl_FragColor = vec4(vec3(v1,v2,mod(0.5,vUv.x)),1.0);
}
