#define GLSLIFY 1
varying vec2 vUv;
uniform float time;
varying float vA;
#define PI 3.14159265359
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void main() {
    vUv = uv;

    vec3 newPos = position;
    vec2 defaultVec = vec2(75.,5.0);
    vec2 diff = defaultVec - newPos.xy;
    float a = smoothstep(-150.,0.0,newPos.z + sin(time)*120.)*smoothstep(0.0,25. + (sin(time)+1.0)*0.5*75.,length(diff));
    vA = a;
    // move space from the center to the vec2(0.0)
    newPos.xy -= defaultVec;
    // rotate the space
    newPos.xy = rotate2d(sin(time*2.5)*PI * a) * newPos.xy;
    // move it back to the original place
    newPos.xy += defaultVec;

    gl_Position = projectionMatrix * modelViewMatrix * vec4( newPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
varying float vA;

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void main() {
    vec2 newUv = vUv;
    vec3 colorA = vec3(newUv.y,newUv.x,fract(sin(newUv.x*1.5 + time)*2.5));
    vec3 colorHSB = rgb2hsb(colorA);
    colorHSB.r += 0.5;

    vec3 finalColor = hsb2rgb(colorHSB);
    gl_FragColor = vec4(finalColor,1.0);
}
