page.vertex:
#define GLSLIFY 1
varying vec2 vUv;
uniform float time;
void main() {
    vUv = uv;

    vec3 newPos = position;
    newPos.xy += vec2(sin(distance(vec2(0.5),newPos.yz)*cos(time*.5)+ time* 8.),0.0);

    gl_Position = projectionMatrix * modelViewMatrix * vec4( newPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
void main() {
    vec2 newUv = vUv;

    gl_FragColor = vec4(newUv.x,newUv.y,0.0,1.0);
}
