page.vertex:
#define GLSLIFY 1
varying vec2 vUv;
uniform float time;
void main() {
    vUv = uv;
    vec3 newPos = position;
    vec3 dif = normalize(newPos - vec3(sin(time*5.),cos(time*5.),0));
    vec3 dotCalc = dif*sin(newPos.x*dif.z*(cos(time)*0.5) + time*10.)*4.5;

    vec3 finalPos = newPos + dotCalc;
    gl_Position = projectionMatrix * modelViewMatrix * vec4( finalPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
void main() {
    vec2 newUv = vUv;
    float dist = distance(newUv,vec2(0));
    float a = abs(sin(dist*2.5+time));
    float b = abs(cos(dist*1.5+time));
    float c = (cos(time)+1.0)/2.0;
    vec3 finalColor = vec3(a,b,c);
    gl_FragColor = vec4(finalColor,1.0);
}
