#define GLSLIFY 1
uniform float time;
varying vec2 vUv;

varying float vR;
varying float vDeg;
varying float vF;

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void main() {
    vec2 newUv = vUv;
    float fA = step(10.,vF);
    vec3 color = vec3(fA);
    vec3 colorA = vec3(newUv.x,newUv.y*(sin(vR + time*15.)*0.5+ 0.5),mod(1.5,vF)*vDeg);

    vec3 colorB = rgb2hsb(colorA);
    colorB.r += vDeg+sin(vR+time*0.5);
    colorB.b *= (sin(vR+ time*0.5)+1.0)*0.5;
    vec3 colorC = hsb2rgb(colorB);
    gl_FragColor = vec4(colorC,1.0);
}
