#define GLSLIFY 1
varying vec2 vUv;
uniform float time;

varying float vDisplace;
void main() {
    vUv = uv;
    vec3 defaultVec = vec3(50.+sin(time*2.5)*25.,0.,-50. + cos(time*2.5)*25.);
    vec3 newPos = position;
    vec3 diff = defaultVec - newPos;
    float disp = smoothstep(20.,60.,length(diff))*sin(time);
    vDisplace = disp;
    newPos+= normalize(diff)*25.0*disp;
    gl_Position = projectionMatrix * modelViewMatrix * vec4( newPos, 1.0 );
}

page.frag:
#define GLSLIFY 1
uniform float time;
varying vec2 vUv;
varying float vDisplace;

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void main() {
    vec2 newUv = vUv;
    vec3 colorA = vec3(newUv.y*newUv.y,newUv.x,newUv.x + newUv.y);
    vec3 colorAHSB = rgb2hsb(colorA);
    colorAHSB.r += 0.7;
    vec3 colorB = hsb2rgb(colorAHSB);
    gl_FragColor = vec4(mix(colorA,colorB,vDisplace),1.0);
}
